create table usermst(id bigint not null auto_increment primary key, name varchar(128) not null, pass_word varchar(128) not null, role varchar(36) not null, mail varchar(128) not null, create_time datetime not null default current_timestamp, update_time datetime not null default current_timestamp on update current_timestamp);
create table scrapingmst(id bigint not null auto_increment primary key,target_url varchar(128) not null unique, html mediumtext not null, create_time datetime(6) not null default current_timestamp(6), update_time datetime(6) not null default current_timestamp(6) on update current_timestamp(6), unique (id,target_url));